	function TestCase_GetRepositoryType(strRepoUrl, vExpected)
	{
		function TestCase_GetRepositoryType_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function GetRepositoryType()',
			GetRepositoryType,
			{
				'strRepoUrl': strRepoUrl
			},
			vExpected,
