sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetLinkDirectoryCICD(vExpected)
	{
		function TestCase_GetLinkDirectoryCICD_CheckResult(vInput,vExpected,vResult)
		{
			var aKeys = x_snc_devtools.GetKeys(vExpected);
			for (var nKey = 0; nKey < aKeys.length; nKey++)
			{
				var aLinkSectionExpected = x_snc_devtools.GetValue(vExpected,aKeys[nKey]);
				var aLinkSection = x_snc_devtools.GetValue(vResult,aKeys[nKey]);
				if (x_snc_devtools.IsArray(aLinkSection) == false)
				{
					return false;
				}
				for (var nExpectedLink = 0; nExpectedLink < aLinkSectionExpected.length; nExpectedLink++)
				{
					var bFound = false;
					var sectionExpected = aLinkSectionExpected[nExpectedLink];
					for (var nLink = 0; nLink < aLinkSection.length; nLink++)
					{
						if (x_snc_devtools.IsEqual(sectionExpected,aLinkSection[nLink]))
						{
							bFound = true;
						}
					}
					if (bFound == false)
					{
						return false;
					}
				}
				
			}
			
			return true;
		}

		test.TestCase(
			'Function GetLinkDirectoryCICD()',
			GetLinkDirectoryCICD,
			{
			},
			vExpected,
			TestCase_GetLinkDirectoryCICD_CheckResult);
	}

	TestCase_GetLinkDirectoryCICD(
		{
			'Helpful links on CICD':
			[
				{
					url: 'https://docs.servicenow.com/csh?topicname=tips-production-deployment.html&version=latest',
					title: 'ServiceNow Documentation - Production deployment tips'
				}
			]
		});
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
