sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetNextPendingDeploymentTaskRecord(vDeployment, vExpected)
	{
		function TestCase_GetNextPendingDeploymentTaskRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == true)
			{
				return x_snc_devtools.IsValidRecord(vResult);
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function GetNextPendingDeplomentTaskRecord()',
			GetNextPendingDeploymentTaskRecord,
			{
				'vDeployment': vDeployment
			},
			vExpected,
			TestCase_GetNextPendingDeploymentTaskRecord_CheckResult);
	}

	TestCase_GetNextPendingDeploymentTaskRecord(null,false);

	var grDeployment = new GlideRecord('x_snc_deployer_deployment');
	grDeployment.initialize();
	grDeployment.insert();

	TestCase_GetNextPendingDeploymentTaskRecord(grDeployment,false);

	var grTask = new GlideRecord('x_snc_deployer_deployment_task');
	grTask.initialize();
	grTask.deployment = grDeployment.sys_id;
	grTask.order = 1;
	grTask.insert();

	TestCase_GetNextPendingDeploymentTaskRecord(grDeployment,false);

	grDeployment.state = GetDeploymentState('inprogress');
	grDeployment.update();

	grTask.state = GetDeploymentTaskState('pending');
	grTask.update();

	TestCase_GetNextPendingDeploymentTaskRecord(grDeployment,true);


	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
