sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_CleanUpPackageObject(vPackageObject, vExpected)
	{
		function TestCase_CleanUpPackageObject_CheckResult(vInput,vExpected,vResult)
		{
			vPackageObject = vInput['vPackageObject'];
			if (x_snc_devtools.IsObject(vPackageObject) == false)
			{
				if (x_snc_devtools.IsValidRecord(vPackageObject) == false)
				{
					return x_snc_devtools.IsEqual(vPackageObject,vResult); 
				}
			}
			var aKeys = x_snc_devtools.GetKeys(vResult);
			if (x_snc_devtools.ArrayValueExists(aKeys,'template') == true)
			{
				return false;
			}
			if (x_snc_devtools.ArrayValueExists(aKeys,'sys_mod_count') == true)
			{
				return false;
			}	
			if (x_snc_devtools.ArrayValueExists(aKeys,'action') == true)
			{
				return false;
			}				
			return true;
		}

		test.TestCase(
			'Function CleanUpPackageObject()',
			CleanUpPackageObject,
			{
				'vPackageObject': vPackageObject
			},
			vExpected,
			TestCase_CleanUpPackageObject_CheckResult);
	}

	TestCase_CleanUpPackageObject(null,null);
	TestCase_CleanUpPackageObject({
		sys_code: null,
		sys_id: '1234',
		name: 'Name',
		template: '',
		active: true,
		action: 'INSERT OR UPDATE',
		sys_mod_count: 100
		},null);
	
	TestCase_CleanUpPackageObject(x_snc_devtools.GetAppRecord('x_snc_deployer'),null);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
